package Class3;

public class RecapOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
/*
 * Addition=> +
 * subtraction=> -
 * Multiplication => *
 * Division => /
 * Modulus => %
 */
		int number1=9;
		int number2=2;
		System.out.println("Answer = "+number1/number2); //dividing number1 bynumber2
		System.out.println("Remainder  = "+number1%number2);
	double number3=9;
	double number4=2;
	System.out.println(number3/number4);//dividing number3 by number4 
	
	
	float number5=1;
	float number6=3;
	System.out.println(number5/number6);
	
	double number7=1;
	double number8=3;
	System.out.println(number7/number8);
	
	
	
	}

}
