
public class VariablesDemo2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		/*
		 * float
		 * double// most widely used
		 * 
		 */
		float box1=12.5f; // create a box of type float and store 12.5 in it the f at the end
		double box2=15.6;
		
		box1=10; //reassigning 10 to box1
		box1=box1+5;// changing again the value of the box
		System.out.println(box1);
		System.out.println(box2);
		
		
	}

}
